import React, { useState } from 'react';
import { motion } from 'framer-motion';

const TeamSelector = ({ title, allPlayers, selectedPlayers, onPlayersChange }) => {
  const [searchTerm, setSearchTerm] = useState('');
  
  const togglePlayer = (player) => {
    const isSelected = selectedPlayers.find(p => p.name === player.name);
    
    if (isSelected) {
      onPlayersChange(selectedPlayers.filter(p => p.name !== player.name));
    } else if (selectedPlayers.length < 11) {
      onPlayersChange([...selectedPlayers, player]);
    }
  };
  
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="cricket-card"
    >
      <h2 className="text-2xl font-bold text-cricket-green mb-4">{title}</h2>
      
      {/* Team Selection */}
      <div className="mb-4">
        <label className="block text-dark-muted mb-2">Select Team</label>
        <select
          value={selectedTeam}
          onChange={(e) => setSelectedTeam(e.target.value)}
          className="cricket-select w-full"
        >
          <option value="">Choose a team...</option>
          {teams.map(team => (
            <option key={team} value={team}>{team}</option>
          ))}
        </select>
      </div>
      
      {/* Selected Players Count */}
      <div className="mb-4 flex items-center justify-between">
        <span className="text-dark-muted">
          Selected: {selectedPlayers.length}/11
        </span>
        {selectedPlayers.length === 11 && (
          <span className="text-cricket-green font-semibold">âœ“ Complete</span>
        )}
      </div>
      
      {/* Player List */}
      {loading ? (
        <div className="text-center py-8 text-dark-muted">Loading players...</div>
      ) : (
        <div className="max-h-96 overflow-y-auto space-y-2">
          {availablePlayers.slice(0, 50).map((player) => {
            const isSelected = selectedPlayers.find(p => p.name === player.name);
            
            return (
              <motion.button
                key={player.name}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => togglePlayer(player)}
                className={`w-full text-left p-3 rounded-lg border transition-all ${
                  isSelected
                    ? 'bg-cricket-green/20 border-cricket-green'
                    : 'bg-dark-card border-dark-border hover:border-cricket-green/50'
                }`}
              >
                <div className="flex items-center justify-between">
                  <span className="font-medium text-white">{player.name}</span>
                  <span className="text-sm text-dark-muted">
                    Avg: {player.batting_avg.toFixed(1)}
                  </span>
                </div>
              </motion.button>
            );
          })}
        </div>
      )}
    </motion.div>
  );
};

export default TeamSelector;
