import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import Header from './components/Header';
import TeamSelector from './components/TeamSelector';
import MatchScenario from './components/MatchScenario';
import PredictionDisplay from './components/PredictionDisplay';
import LoadingSpinner from './components/LoadingSpinner';
import api from './utils/api';

function App() {
  const [allPlayers, setAllPlayers] = useState([]);
  const [venues, setVenues] = useState([]);
  const [loading, setLoading] = useState(true);
  const [predicting, setPredicting] = useState(false);
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState(null);
  
  // Team selection - Both are complete teams (11 players each)
  const [selectedBattingPlayers, setSelectedBattingPlayers] = useState([]);
  const [selectedOppositionPlayers, setSelectedOppositionPlayers] = useState([]);
  
  // Match scenario
  const [matchScenario, setMatchScenario] = useState({
    venue: '',
    venue_avg_score: 250,
    current_score: 0,
    wickets_fallen: 0,
    overs: 0,
    runs_last_10: 0,
    batsman_1: '',
    batsman_2: ''
  });
  
  // Load initial data
  useEffect(() => {
    const loadData = async () => {
      try {
        const [playersRes, venuesRes] = await Promise.all([
          api.getPlayers(),
          api.getVenues()
        ]);
        
        setAllPlayers(playersRes.data.players);
        setVenues(venuesRes.data.venues);
        setLoading(false);
      } catch (err) {
        setError('Failed to load data. Make sure backend is running on port 5002');
        console.error('Load error:', err);
        setLoading(false);
      }
    };
    
    loadData();
  }, []);
  
  // Handle prediction
  const handlePredict = async () => {
    if (selectedBattingPlayers.length < 11) {
      setError('Please select 11 batting team players');
      return;
    }
    
    if (selectedOppositionPlayers.length < 11) {
      setError('Please select 11 opposition team players');
      return;
    }
    
    if (!matchScenario.venue) {
      setError('Please select a venue');
      return;
    }
    
    setPredicting(true);
    setError(null);
    
    try {
      const balls_bowled = matchScenario.overs * 6;
      
      const response = await api.predict({
        batting_team_players: selectedBattingPlayers.map(p => p.name),
        bowling_team_players: selectedOppositionPlayers.map(p => p.name),
        venue: matchScenario.venue,
        venue_avg_score: matchScenario.venue_avg_score,
        current_score: matchScenario.current_score,
        wickets_fallen: matchScenario.wickets_fallen,
        balls_bowled: balls_bowled,
        runs_last_10_overs: matchScenario.runs_last_10,
        batsman_1: matchScenario.batsman_1,
        batsman_2: matchScenario.batsman_2
      });
      
      setPrediction(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Prediction failed. Check console for details.');
      console.error('Prediction error:', err);
    } finally {
      setPredicting(false);
    }
  };
  
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }
  
  return (
    <div className="min-h-screen bg-dark-bg">
      <Header />
      
      <main className="max-w-7xl mx-auto px-6 py-8">
        {error && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className="mb-6 bg-red-900/20 border border-red-500/50 rounded-lg p-4 text-red-400"
          >
            {error}
            <button 
              onClick={() => setError(null)}
              className="ml-4 text-sm underline"
            >
              Dismiss
            </button>
          </motion.div>
        )}
        
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Batting Team Selection */}
          <TeamSelector
            title="Select Batting Team (11 Players)"
            allPlayers={allPlayers}
            selectedPlayers={selectedBattingPlayers}
            onPlayersChange={setSelectedBattingPlayers}
          />
          
          {/* Opposition Team Selection */}
          <TeamSelector
            title="Select Opposition Team (11 Players)"
            allPlayers={allPlayers}
            selectedPlayers={selectedOppositionPlayers}
            onPlayersChange={setSelectedOppositionPlayers}
          />
        </div>
        
        {/* Match Scenario */}
        <MatchScenario
          scenario={matchScenario}
          onChange={setMatchScenario}
          venues={venues}
          battingPlayers={selectedBattingPlayers}
        />
        
        {/* Predict Button */}
        <div className="text-center my-8">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handlePredict}
            disabled={predicting}
            className="cricket-button text-xl px-12 py-4 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {predicting ? 'Predicting...' : 'üèè Predict Final Score'}
          </motion.button>
        </div>
        
        {/* Prediction Results */}
        {prediction && (
          <PredictionDisplay prediction={prediction} scenario={matchScenario} />
        )}
      </main>
    </div>
  );
}

export default App;
